body.script:
$(document).ready(function(){
	/*play-pause*/
	$(".nav__list__play-pause").on("touchstart", function() {
		$(this).css("opacity", "0.5");
	});
	$(".nav__list__play-pause").on("touchend", function() {
		if ($(this).attr("src") == "/js/images/play.png") {
			$(this).attr("src", "/js/images/pause.png");
			console.log("pressed play");
		}
		else {
			$(this).attr("src", "/js/images/play.png");
			console.log("pressed pause");
		}
		$(this).css("opacity", "1");
	});


	/*restart*/
	$(".nav_list__restart").on("touchstart", function() {
		$(this).css("opacity", "0.5");
	});
	$(".nav_list__restart").on("touchend", function() {
		$(this).css("opacity", "1");
		console.log("pressed restart");
	});

	/*button left and right*/
	$(".pointer-left__img-left").on("touchstart", function() {
		$(this).css("opacity", "0.5");
	});
	$(".pointer-left__img-left").on("touchend", function() {
		$(this).css("opacity", "1");
		console.log("pressed button-left");
	});

	$(".pointer-right__img-right").on("touchstart", function() {
		$(this).css("opacity", "0.5");
	});
	$(".pointer-right__img-right").on("touchend", function() {
		$(this).css("opacity", "1");
		console.log("pressed button-right");
	});

});
